package codechef;

import java.util.Scanner;

public class Fill_The_Bucket {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int t = sc.nextInt();
		while (t-- > 0) {
			int k = sc.nextInt();
			int x = sc.nextInt();
			System.out.println(k - x);
		}

	}

}
