package codechef;

import java.util.Scanner;

public class Squats {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int t = sc.nextInt();
		while (t-- > 0) {
			int x = sc.nextInt();
			System.out.println(x * 15);
		}

	}

}
